    @Test
    public void 入力例1() throws Exception {
        String input =
                "4 4" + System.lineSeparator() +
                        "##.." + System.lineSeparator() +
                        "##.." + System.lineSeparator() +
                        "..##" + System.lineSeparator() +
                        "..##";
        String output =
                "possible" + System.lineSeparator() +
                        "#..." + System.lineSeparator() +
                        "...." + System.lineSeparator() +
                        "...." + System.lineSeparator() +
                        "...#";

        assertIO(input, output);
    }

